package br.com.generation.aula01;

public class Exvar02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int var1;
		double var2;
	    
		
		var1 = 10;
		var2 = 10.0;
		
		System.out.println("Valor Original da 1 variavel: "  +var1);

		System.out.println("Valor Original da 2 variavel: "  +var2);
		
		
		var1 = var1 / 4;
		var2 = var2 / 4;
		

		System.out.println("Valor da 1 variavel apos a divisao: "  +var1);
		System.out.println("Valor da 2 variavel apos a divisao: "  +var2);
	}	

}
